mapper.select:
select
person.person_id as personId, person.person_name as personName, person.city as personCity,
person.county as personCounty, person.school as personSchool, person.wschool as
personWschool,person.person_class as personClass,
person.phone as phone, person.state as personState, person.create_time as createTime,
person.update_time as updateTime,article.article_id as articleId
from person_info AS person LEFT JOIN article_file as article ON
person.person_id=article.person_id
where person.state=1


		AND person.person_name = #{personName,jdbcType=VARCHAR}
		AND person.phone = #{phone,jdbcType=VARCHAR}


		AND article.article_type = #{articleType,jdbcType=VARCHAR}



	AND person.person_id = #{personId,jdbcType=BIGINT}

SELECT c.city_id AS cityId, c.city_name AS cityName, co.county_id AS countyId, co.county_name AS countyName FROM city AS c LEFT JOIN county AS co ON c.city_id = co.city_id WHERE 1=1
AND c.city_id = #{cityId,jdbcType=VARCHAR}
AND co.county_id = #{countyId,jdbcType=VARCHAR}

SELECT co.county_id AS countyId, co.county_name AS countyName, s.school_id AS schoolId, s.school_name AS schoolName FROM county AS co LEFT JOIN school AS s ON co.county_id = s.county_id WHERE 1 = 1
AND co.county_id = #{countyId,jdbcType=VARCHAR}
AND s.school_id = #{schoolId,jdbcType=VARCHAR}
